@if ($paginator->hasPages())
        <?php
        $currentUrl = url()->full(); // Get the full current URL
        $nextPageUrl = $paginator->nextPageUrl(); // Get the next page URL generated by $paginator

        // Parse the current URL to extract the query parameters
        $currentQueryParams = [];
        if ($query = parse_url($currentUrl, PHP_URL_QUERY)) {
            parse_str($query, $currentQueryParams);
        }

        // Append the current query parameters to the next page URL if they are present
        if (!empty($currentQueryParams)) {
            $nextPageUrl .= (str_contains($nextPageUrl, '?') ? '&' : '?') . http_build_query(
                    $currentQueryParams
                );
        }
        ?>
    <nav>
        <ul class="pagination">
            {{-- Previous Page Link --}}
            @if ($paginator->onFirstPage())
                <li class="page-item disabled" aria-disabled="true" aria-label="@lang('pagination.previous')">
                    <span class="page-link" aria-hidden="true">&lsaquo;</span>
                </li>
            @else
                <li class="page-item">
                    <a class="page-link" href="{{ $paginator->previousPageUrl() }}" rel="prev"
                       aria-label="@lang('pagination.previous')">&lsaquo;</a>
                </li>
            @endif

            {{-- Pagination Elements --}}
            @foreach ($elements as $element)
                {{-- "Three Dots" Separator --}}
                @if (is_string($element))
                    <li class="page-item disabled" aria-disabled="true"><span class="page-link">{{ $element }}</span>
                    </li>
                @endif

                {{-- Array Of Links --}}
                @if (is_array($element))
                    @foreach ($element as $page => $url)
                        @if ($page == $paginator->currentPage())
                            <li class="page-item active" aria-current="page"><span class="page-link">{{ $page }}</span>
                            </li>
                        @else
                            <li class="page-item"><a class="page-link" href="{{ $url }}">{{ $page }}</a></li>
                        @endif
                    @endforeach
                @endif
            @endforeach

            {{-- Next Page Link --}}
            @if ($paginator->hasMorePages())
                <li class="page-item">
                    <a href="{{ $paginator->hasMorePages() && !empty($currentQueryParams) ? $nextPageUrl : $paginator->nextPageUrl() }}"
                       class="page-link" aria-label="@lang('pagination.next')">&rsaquo;</a>
                </li>
            @else
                <li class="page-item disabled" aria-disabled="true" aria-label="@lang('pagination.next')">
                    <span class="page-link" aria-hidden="true">&rsaquo;</span>
                </li>
            @endif
        </ul>
    </nav>
@endif
