@if ($paginator->hasPages())
    @if ($paginator->hasPages())
            <?php
            $currentUrl = url()->full(); // Get the full current URL
            $nextPageUrl = $paginator->nextPageUrl(); // Get the next page URL generated by $paginator
            $prevPageUrl = $paginator->previousPageUrl(); // Get the previous page URL generated by $paginator

            // Parse the current URL to extract the query parameters
            $currentQueryParams = [];
            if ($query = parse_url($currentUrl, PHP_URL_QUERY)) {
                parse_str($query, $currentQueryParams);
            }

            // Append the current query parameters to the next page URL if they are present
            if (!empty($currentQueryParams)) {
                $nextPageUrl .= (str_contains($nextPageUrl, '?') ? '&' : '?') . http_build_query(
                        $currentQueryParams
                    );
                $prevPageUrl .= (str_contains($prevPageUrl, '?') ? '&' : '?') . http_build_query(
                        $currentQueryParams
                    );
            }

            // Decrement the page number in prevPageUrl if it's greater than 1
            if (isset($currentQueryParams['page']) && $currentQueryParams['page'] > 1) {
                $prevPageUrl = str_replace(
                    'page=' . $currentQueryParams['page'],
                    'page=' . ($currentQueryParams['page'] - 1),
                    $prevPageUrl
                );
            }
            ?>
    @endif
    <nav role="navigation" aria-label="{{ __('Pagination Navigation') }}" class="flex items-center justify-between">
        <div class="flex justify-between flex-1 sm:hidden">
            @if ($paginator->onFirstPage())
                <span
                    class="relative inline-flex items-center px-4 py-2 text-sm font-medium text-gray-500 bg-white border border-gray-300 cursor-default leading-5 rounded-md">
                    {!! __('pagination.previous') !!}
                </span>
            @else
                <a href="{{ $prevPageUrl ?? $paginator->url(1) }}"
                   class="relative inline-flex items-center px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 leading-5 rounded-md hover:text-gray-500 focus:outline-none focus:ring ring-gray-300 focus:border-blue-300 active:bg-gray-100 active:text-gray-700 transition ease-in-out duration-150">
                    {!! __('pagination.previous') !!}
                </a>
            @endif

            @if ($paginator->hasMorePages())
                <a href="{{ $nextPageUrl }}"
                   class="relative inline-flex items-center px-4 py-2 ml-3 text-sm font-medium text-gray-700 bg-white border border-gray-300 leading-5 rounded-md hover:text-gray-500 focus:outline-none focus:ring ring-gray-300 focus:border-blue-300 active:bg-gray-100 active:text-gray-700 transition ease-in-out duration-150">
                    {!! __('pagination.next') !!}
                </a>
            @else
                <span
                    class="relative inline-flex items-center px-4 py-2 ml-3 text-sm font-medium text-gray-500 bg-white border border-gray-300 cursor-default leading-5 rounded-md">
                    {!! __('pagination.next') !!}
                </span>
            @endif
        </div>

        <div class="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
            <div>
                <p class="text-sm text-gray-700 leading-5">
                    {!! __('Showing') !!}
                    @if ($paginator->firstItem())
                        <span class="font-medium">{{ $paginator->firstItem() }}</span>
                        {!! __('to') !!}
                        <span class="font-medium">{{ $paginator->lastItem() }}</span>
                    @else
                        {{ $paginator->count() }}
                    @endif
                    {!! __('of') !!}
                    <span class="font-medium">{{ $paginator->total() }}</span>
                    {!! __('results') !!}
                </p>
            </div>
        </div>
    </nav>
@endif
